mapper.select:
select
from t_card c where c.dr=1
and trim(replace(c.title,' ','')) like trim(replace ('%' #{title,jdbcType=CHAR} '%' ,' ',''))
and c.create_time >= #{startTime,jdbcType=TIMESTAMP}
and c.create_time <= #{endTime,jdbcType=TIMESTAMP}
and trim(replace(concat(c.initial_word, c.initial_num),' ','')) like trim(replace ('%' #{cardNum,jdbcType=CHAR} '%' ,' ',''))

select
from t_card c where c.dr = 1 and c.status = 2
and trim(replace(c.title,' ','')) like trim(replace ('%' #{title,jdbcType=CHAR} '%' ,' ',''))
and c.create_time >= #{startTime}
and c.create_time <= #{endTime}
and trim(replace(c.initial_num,' ','')) like trim(replace ('%' #{cardNum,jdbcType=CHAR} '%' ,' ',''))
