mapper.select:
select

from po_compliance


        policy_id = #{policyId,jdbcType=BIGINT}


        and talent_id = #{talentId,jdbcType=BIGINT}


        and `year` = #{year,jdbcType=BIGINT}

select policy_id as policyId, sum(CASE WHEN po.`status`=11 THEN 1 ELSE 0 END) as notApply, sum(CASE WHEN po.`status`=1 THEN 1 ELSE 0 END) as pass, sum(CASE WHEN po.`status`=2 THEN 1 ELSE 0 END) as reject, sum(CASE WHEN po.`status`=3 THEN 1 ELSE 0 END) as notApproval from po_compliance po
policy_id = #{policyId,jdbcType=BIGINT}
and `year` = #{year,jdbcType=BIGINT}
group by policy_id

select

from po_compliance


        talent_id = #{talentId,jdbcType=BIGINT}


        and year = #{year,jdbcType=BIGINT}
