mapper.update:
update t_policy
set dr = 2
where policy_id = #{policyId,jdbcType=BIGINT}

mapper.select:
select

from t_policy where dr = 1
and create_time >= #{start}
and create_time <= #{end}
and `name` like '%' #{name} '%'
and num like '%' #{num} '%'
and apply = #{apply}

select

from t_policy
where dr = 1 and num = #{num,jdbcType=CHAR}
