mapper.select:
select

from t_policy


        p_tid = #{pTid,jdbcType=BIGINT}


        and dr = #{dr,jdbcType=TINYINT}


        and up_down = #{upDown,jdbcType=TINYINT}

select p.name as policyName, p.num as num, p.start_time as startTime, p.end_time as endTime, pt.p_type_name as policyTypeName, r.name as roleName, p.update_time as updateTime, p.up_down as upDown, p.policy_id as policyId, pt.status as policyTypeUpDown from t_policy p left join t_role r on p.role_id = r.role_id left join po_type pt on p.p_tId = pt.p_tId where p.dr = 1
and p.update_time >= #{start}
and p.update_time <= #{end}
and p.name like '%' #{name} '%' and p.num like '%' #{num} '%'
and p.p_tId = #{policyType, jdbcType=TINYINT}
and p.role_id = #{responsibleUnitId,jdbcType=BIGINT}
and r.role_id = #{roleId,jdbcType=BIGINT}
and (p.start_time <= #{currentTime} and p.end_time >= #{currentTime} and p.up_down = 1)
and (p.start_time >= #{currentTime} and p.up_down = 1)
and (p.end_time <= #{currentTime} and p.up_down = 1)
and p.up_down = 2
order by p.up_down asc, p.update_time desc

select

from t_policy
where dr = 1 and num = #{num,jdbcType=CHAR}
